+            ]}>
+              {profile?.gender || "Set Now"}
+            </Text>
+            <MaterialIcons name="edit" size={20} color="#888" style={{ marginLeft: 5 }} />
+          </View>
+        </View>
+
+        <View style={styles.infoRow}>
+          <Text style={styles.label}>Age:</Text>
+          <View style={styles.row}>
+            <Text style={[
+              styles.value,
+              { color: profile?.age ? "#000" : "#999" },
+            ]}>
+              {profile?.age || "Set Now"}
+            </Text>
+            <MaterialIcons name="edit" size={20} color="#888" style={{ marginLeft: 5 }} />
