import React, { useState } from "react";
import { View, Text, TouchableOpacity, StyleSheet, Dimensions, TextInput, StatusBar } from "react-native";
import { MaterialIcons } from "@expo/vector-icons"; 
import { useRouter } from "expo-router";

const { width } = Dimensions.get("window");

export default function welcome() {
  const [email, setEmail] = useState(""); // Store user email
  const [pass, setPass] = useState(""); // Store user password
  const [isEditing, setIsEditing] = useState(false); 
  const router = useRouter();
  return (
    <View style={styles.container}>
      <View style={{paddingTop: 40}}>
        <StatusBar barStyle="dark-content" translucent={true} backgroundColor="transparent" />
      </View>
      <View style={styles.content}>
        <TouchableOpacity onPress={() => router.push("/location/welcome")}>
          <Text style={styles.back}>Back</Text>
        </TouchableOpacity>
        <Text style={styles.title}>Sign in with your registered email or phone number</Text>
        <View style={styles.inputContainer}>
          <TextInput
            style={styles.input}
            placeholder="Email or Phone Number"
            placeholderTextColor="#A0A0A0"
            value={email}
            onChangeText={setEmail}
            editable={isEditing}
          />
        </View>
        <View style={styles.inputContainer}>
          <TextInput
            style={styles.input}
            placeholder="Enter Your Password"
            placeholderTextColor="#A0A0A0"
            value={pass}
            onChangeText={setPass}
            editable={isEditing}
          />
          <TouchableOpacity onPress={() => setIsEditing(true)}>
            <MaterialIcons name="edit" size={20} color="#5089A3" />
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
    alignItems: "center",
  },
  content: {
    width: width * 0.8,
    alignItems: "center",
  },
  back: {
    padding: 10,
    fontSize: 18,
    fontFamily: "Poppins-Regular",
    color: "#414141",
    letterSpacing: 1,
    marginLeft: -20,
  },
  title: {
    fontSize: 22,
    fontWeight: "600",
    color: "#414141",
    marginLeft: -20,
  },
  subtitle: {
    fontSize: 16,
    color: "#A0A0A0",
    textAlign: "center",
    marginTop: 5,
  },
  inputContainer: {
    flexDirection: "row",
    alignItems: "center",
    borderWidth: 1,
    borderColor: "#D1D1D1",
    borderRadius: 8,
    paddingHorizontal: 10,
    width: "100%",
    marginTop: 20,
  },
  input: {
    flex: 1,
    height: 40,
    fontSize: 16,
    color: "#414141",
  },
});
